<div class="clearfix">	<a href="/node/<?=$node->alias?>.html" title="<?=$node->title?>"><h3><?=$node->title?></h3></a><div>	<?php if(isset($node->terms["tags"]) && !empty($node->terms["tags"])){?>	<i class="icon-tag"></i> 		<?php 				$tagsArr=array();				foreach ($node->terms["tags"] as $tag)$tagsArr[]='<a href="/term/'.$tag->type.'/'.$tag->alias.'">'.$tag->title.'</a>';			?>		<?=implode(', ', $tagsArr); ?>	<?php }?></div><p><?=word_limiter($node->body,100)?></p><a class="btn" href="/node/<?=$node->alias?>.html">Detail<i class="icon-arrow-right"></i></a><br style="clear:both"><div class="span5 well" style="margin-top:10px; margin-left:0px; min-height:10px; padding:5px;">	<small class="muted"><?=$node->add_date?></small></div></div>