<div class="pagination">  <ul>    <li class="<?php if($current_page==$prev_page)print 'disabled';?>"><a href="<?=$root_url.$prev_page?>">Prev</a></li>	<?php foreach($pages as $page){?>		<li class="<?php if($current_page==$page)print 'active';?>"><a href="<?=$root_url.$page?>"><?=$page?></a></li>	<?php }?>    <li class="<?php if($current_page==$next_page)print 'disabled';?>"><a href="<?=$root_url.$next_page?>">Next</a></li>  </ul></div>